CREATE TABLE IF NOT EXISTS `_cic_folders`  (`id` INT UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'ID', `parent` INT  NOT NULL DEFAULT 0 COMMENT 'Родитель (папка)', `name` VARCHAR (255) NOT NULL DEFAULT '' COMMENT 'Название категории (папки)', `insdate` TIMESTAMP  NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Дата создания', `update` TIMESTAMP  NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Дата изменения записи', PRIMARY KEY (`id`)) COMMENT='Папки'	;
CREATE TABLE IF NOT EXISTS `_cic_pages`  (`id` INT UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'ID', `parent` INT  NOT NULL DEFAULT 0 COMMENT 'Родитель (папка)', `name` VARCHAR (255) NOT NULL DEFAULT '' COMMENT 'Название страницы', `note` VARCHAR (255) NOT NULL DEFAULT '' COMMENT 'Примечание к странице', `insdate` TIMESTAMP  NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Дата добавления записи', `update` TIMESTAMP  NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Дата изменения записи', PRIMARY KEY (`id`), UNIQUE KEY (`name`)) COMMENT='Страницы'	;
CREATE TABLE IF NOT EXISTS `_cic_container`  (`id` INT UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'ID', `page` INT  NOT NULL DEFAULT 0 COMMENT 'Страница', `name` VARCHAR (255) NOT NULL DEFAULT '' COMMENT 'Название контейнера', `order` INT  NOT NULL DEFAULT 0 COMMENT 'Порядковый номер', `content` TEXT  NOT NULL  COMMENT 'Контент', `note` VARCHAR (255) NOT NULL DEFAULT '' COMMENT 'Примечание', `insdate` TIMESTAMP  NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Дата добавления записи', `update` TIMESTAMP  NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Дата изменения записи', PRIMARY KEY (`id`)) COMMENT='Контейнер'	;
CREATE TABLE IF NOT EXISTS `_cic_config`  (`key` VARCHAR (64) NOT NULL DEFAULT '' COMMENT 'Ключ', `value` VARCHAR (255) NOT NULL DEFAULT '' COMMENT 'Значение', `description` VARCHAR (255) NOT NULL DEFAULT '' COMMENT 'Описание', `opt1` INT    COMMENT 'Опция№1', `opt2` VARCHAR (255)   COMMENT 'Опция№2', `group` INT    COMMENT 'Группа конфигурации') COMMENT='Конфигурация'	;
